definition.script:
node {
   stage('Build,Test,and Scan') { // for display purposes
      // Get some code from a GitHub repository
        git branch: 'master', credentialsId: 'Github', url: 'https://github.com/Mbwata/devops-bootcamp.git'
        flywayrunner commandLineArgs: '', credentialsId: 'dierolldb', flywayCommand: 'clean', installationName: 'Flyway1', locations: 'filesystem:' + env.WORKSPACE + '/sql', url: 'jdbc:postgresql://die-roll-db.dieroll:5432/dieroll'
        flywayrunner commandLineArgs: '', credentialsId: 'dierolldb', flywayCommand: 'migrate', installationName: 'Flyway1', locations: 'filesystem:' + env.WORKSPACE + '/sql', url: 'jdbc:postgresql://die-roll-db.dieroll:5432/dieroll'
        nodejs('Node1') {
            sh label: '', script: '''npm install
            npm test'''
            sh label: '', script: '/var/jenkins_home/tools/hudson.plugins.sonar.SonarRunnerInstallation/Scanner1/bin/sonar-scanner -Dsonar.host.url=http://sonarqube.sonarqube:9000 -Dsonar.projectBaseDir='+ env.WORKSPACE
        }
   }
   stage('Push to repos') {
        withCredentials([usernamePassword(credentialsId: 'nexus', passwordVariable: 'nexuspw', usernameVariable: 'nexususer')]) {
            sh label: '', script: '''docker login nexus-repo.localhost -u ${nexususer} -p ${nexuspw}
            docker build -t nexus-repo.localhost/dieroll:latest .
            docker tag nexus-repo.localhost/dieroll:latest nexus-repo.localhost/dieroll:v${BUILD_NUMBER}
            docker push nexus-repo.localhost/dieroll:latest
            docker push nexus-repo.localhost/dieroll:v${BUILD_NUMBER}'''

            fileOperations([fileZipOperation('./sql')])

            sh label: '', script: '''curl -v --user ${nexususer}:${nexuspw} --upload-file sql.zip http://nexus.nexus:9081/repository/dieroll-sql/sql_v${BUILD_NUMBER}.zip
            curl -v --user ${nexususer}:${nexuspw} --upload-file sql.zip http://nexus.nexus:9081/repository/dieroll-sql/sql_latest.zip'''

            build job: 'dev-deployment', parameters: [string(name: 'Version', value: 'v'+ env.BUILD_NUMBER)]
        }
   }
}
