        flywayrunner commandLineArgs: '-schemas=dieroll', credentialsId: '75a61263-932f-4673-809c-13a3aab46230', flywayCommand: 'clean', installationName: 'Flyway1', locations: 'filesystem:' + env.WORKSPACE + '/sql', url: 'jdbc:postgresql://die-roll-db.jenkins:5432/dieroll'
        flywayrunner commandLineArgs: '-schemas=dieroll', credentialsId: '75a61263-932f-4673-809c-13a3aab46230', flywayCommand: 'migrate', installationName: 'Flyway1', locations: 'filesystem:' + env.WORKSPACE + '/sql', url: 'jdbc:postgresql://die-roll-db.jenkins:5432/dieroll'
        nodejs('Node1') {
