            npm test'''
           // sh label: '', script: '/var/jenkins_home/tools/hudson.plugins.sonar.SonarRunnerInstallation/SonarScanner/bin/sonar-scanner -Dsonar.host.url=http://sonarqube.sonarqube:9442 -Dsonar.projectBaseDir='+ env.WORKSPACE
        }
   }
   stage('Push to repos') {
        withCredentials([usernamePassword(credentialsId: '9f16dc79-34e4-406b-88ae-ebaade6690ec', passwordVariable: 'nexuspw', usernameVariable: 'nexususer')]) {
            sh label: '', script: '''docker login nexus-repo.localhost -u ${nexususer} -p ${nexuspw}
            docker build -t nexus-repo.localhost/die-roll:latest .
            docker tag nexus-repo.localhost/die-roll:latest nexus-repo.localhost/die-roll:v${BUILD_NUMBER}
            docker push nexus-repo.localhost/die-roll:latest
            docker push nexus-repo.localhost/die-roll:v${BUILD_NUMBER}'''

            fileOperations([fileZipOperation('./sql')])

            sh label: '', script: '''curl -v --user ${nexususer}:${nexuspw} --upload-file sql.zip http://nexus.nexus:9081/repository/die-roll-sql/sql_v${BUILD_NUMBER}.zip
            curl -v --user ${nexususer}:${nexuspw} --upload-file sql.zip http://nexus.nexus:9081/repository/die-roll-sql/sql_latest.zip
            helm package helm
            curl -u ${nexususer}:${nexuspw} http://nexus.nexus:9081/repository/die-roll-helm/ --upload-file die-roll-1.0.0.tgz -v'''

        }
   }
   stage('Deploy to Dev') {
        cleanWs()
        withCredentials([usernamePassword(credentialsId: '9f16dc79-34e4-406b-88ae-ebaade6690ec', passwordVariable: 'nexuspw', usernameVariable: 'nexususer')]) {
            sh label: '', script: '''            wget --user=${nexususer} --password=${nexuspw} http://nexus.nexus:9081/repository/die-roll-sql/sql_v${BUILD_NUMBER}.zip'''

        fileOperations([fileUnZipOperation(filePath: 'sql_v${BUILD_NUMBER}.zip', targetLocation: './')])
        flywayrunner commandLineArgs: '-schemas=dieroll', credentialsId: '75a61263-932f-4673-809c-13a3aab46230', flywayCommand: 'migrate', installationName: 'Flyway1', locations: 'filesystem:' + env.WORKSPACE + '/sql', url: 'jdbc:postgresql://die-roll-db.dieroll-dev:5432/dieroll'

        sh label: '', script: '''helm repo add nexus http://nexus.nexus:9081/repository/die-roll-helm/
        #helm repo update
        helm upgrade dieroll-dev nexus/die-roll --debug --set image.tag=${Version},metadata.namespace=dieroll-dev'''


